mapper.select:
SELECT asset_id as "id", SUM(CASE WHEN fao.type = 'DAILY' THEN 1 ELSE 0 END) AS "dailyCount", MIN(CASE WHEN fao.type = 'DAILY' THEN fao.date_time ELSE NULL END) AS "dailyMinDateTime", MAX(CASE WHEN fao.type = 'DAILY' THEN fao.date_time ELSE NULL END) AS "dailyMaxDateTime", SUM(CASE WHEN fao.type = 'MINUTE' THEN 1 ELSE 0 END) AS "minuteCount", MIN(CASE WHEN fao.type = 'MINUTE' THEN fao.date_time ELSE NULL END) AS "minuteMinDateTime", MAX(CASE WHEN fao.type = 'MINUTE' THEN fao.date_time ELSE NULL END) AS "minuteMaxDateTime" FROM fintics_asset_ohlcv fao WHERE 1 = 1
AND fao.asset_id = #{assetId}
GROUP BY fao.asset_id

SELECT indice_id AS "id", SUM(CASE WHEN type = 'DAILY' THEN 1 ELSE 0 END) AS "dailyCount", MIN(CASE WHEN type = 'DAILY' THEN date_time ELSE NULL END) AS "dailyMinDateTime", MAX(CASE WHEN type = 'DAILY' THEN date_time ELSE NULL END) AS "dailyMaxDateTime", SUM(CASE WHEN type = 'MINUTE' THEN 1 ELSE 0 END) AS "minuteCount", MIN(CASE WHEN type = 'MINUTE' THEN date_time ELSE NULL END) AS "minuteMinDateTime", MAX(CASE WHEN type = 'MINUTE' THEN date_time ELSE NULL END) AS "minuteMaxDateTime" FROM fintics_indice_ohlcv WHERE 1 = 1
AND indice_id = #{indiceId}
GROUP BY indice_id
