mapper.select:
SELECT fa.asset_id as "assetId", fa.name as "name", fa.market as "market", fa.exchange as "exchange", fa.type as "type", fa.market_cap as "marketCap" FROM fintics_asset fa WHERE 1 = 1
AND asset_id like #{assetId}
AND name like #{name}
AND market = #{market}

SELECT asset_id as "assetId", SUM(CASE WHEN fo.type = 'DAILY' THEN 1 ELSE 0 END) AS "dailyCount", MIN(CASE WHEN fo.type = 'DAILY' THEN fo.date_time ELSE NULL END) AS "dailyMinDateTime", MAX(CASE WHEN fo.type = 'DAILY' THEN fo.date_time ELSE NULL END) AS "dailyMaxDateTime", SUM(CASE WHEN fo.type = 'MINUTE' THEN 1 ELSE 0 END) AS "minuteCount", MIN(CASE WHEN fo.type = 'MINUTE' THEN fo.date_time ELSE NULL END) AS "minuteMinDateTime", MAX(CASE WHEN fo.type = 'MINUTE' THEN fo.date_time ELSE NULL END) AS "minuteMaxDateTime" FROM fintics_ohlcv fo WHERE 1 = 1
AND fo.asset_id = #{assetId}
GROUP BY fo.asset_id
